import 'package:flutter/material.dart';

class MockupScreenTwo extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: SafeArea(
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Container(
              decoration: BoxDecoration(
                image: DecorationImage(
                  image: NetworkImage('data:image/jpeg;base64,'),
                  fit: BoxFit.cover,
                  colorFilter: ColorFilter.mode(Colors.black.withOpacity(0.5), BlendMode.dstATop),
                ),
              ),
              padding: EdgeInsets.fromLTRB(16.0, 24.0, 16.0, 24.0),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Text(
                    'Jumanji: The Next Level',
                    style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold, color: Colors.white),
                  ),
                  SizedBox(height: 4.0),
                  Text(
                    'Action • Adventure • Comedy • Fantasy',
                    style: TextStyle(fontSize: 14, color: Colors.white70),
                  ),
                ],
              ),
            ),
            Padding(
              padding: const EdgeInsets.symmetric(horizontal: 16.0, vertical: 8.0),
              child: Text(
                'What\'s Popular',
                style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white),
              ),
            ),
            _buildMovieRow(
              imageUrl: 'data:image/jpeg;base64,',
              title: 'Birds of Prey',
              rating: '7.2',
              description: 'Action, Crime, Comedy, Drama',
            ),
            _buildMovieRow(
              imageUrl: 'data:image/jpeg;base64,',
              title: 'Red Sparrow',
              rating: '6.5',
              description: 'Mystery, Thriller',
            ),
            Padding(
              padding: const EdgeInsets.symmetric(horizontal: 16.0, vertical: 8.0),
              child: Text(
                'Now Playing',
                style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white),
              ),
            ),
            _buildMovieRow(
              imageUrl: 'data:image/jpeg;base64,',
              title: 'To All the Boys: P.S. I Still Love You',
              rating: '6.9',
              description: 'Romance, Comedy',
            ),
            _buildMovieRow(
              imageUrl: 'data:image/jpeg;base64,',
              title: 'Ford v Ferrari',
              rating: '7.2',
              description: 'Drama, Action',
            ),
          ],
        ),
      ),
    );
  }

  Widget _buildMovieRow({required String imageUrl, required String title, required String rating, required String description}) {
    return Padding(
      padding: const EdgeInsets.symmetric(horizontal: 16.0, vertical: 8.0),
      child: Row(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          ClipRRect(
            borderRadius: BorderRadius.circular(8.0),
            child: Image.network(
              imageUrl,
              width: 60,
              height: 90,
              fit: BoxFit.cover,
              errorBuilder: (context, error, stackTrace) {
                return Container(
                  width: 60,
                  height: 90,
                  color: Colors.grey,
                  child: Center(child: Text('Image Failed', style: TextStyle(color: Colors.white))),
                );
              },
            ),
          ),
          SizedBox(width: 16.0),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  title,
                  style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold, color: Colors.white),
                ),
                SizedBox(height: 4.0),
                Text(
                  description,
                  style: TextStyle(fontSize: 14, color: Colors.grey[400]),
                ),
                SizedBox(height: 4.0),
                Row(
                  children: [
                    Icon(Icons.star, color: Colors.yellow, size: 16),
                    SizedBox(width: 4.0),
                    Text(rating, style: TextStyle(fontSize: 14, color: Colors.white)),
                  ],
                ),
              ],
            ),
          ),
          Icon(Icons.arrow_forward, color: Colors.grey),
        ],
      ),
    );
  }
}